// Creating a logger, as a way of using the event emitter

const EventEmitter = require('events');
const uuid = require('uuid');


// const Logger = require('./logger');

// const logger = new Logger();

// Generating random IDs using the uuid module
console.log((uuid.v4()));
console.log((uuid.v4()));
console.log((uuid.v4()));
console.log((uuid.v4()));

class Logger extends EventEmitter {
    log(msg) {
        // Call/ raise an event
        this.emit('message', { id: uuid.v4(), msg });
    }
}


