// OS Module gives us info about our environment, operating syst. etc

const os = require('os');

//Platform - tells you what platform your op syst is running (linux for me)
console.log(os.platform());

//CPU architecture - tells you your CPU architecture
console.log(os.arch());

// CPU Core Info - gives you an object with information for every core on your machine
console.log(os.cpus())

// Free memory
console.log(os.freemem());

// Total memory
console.log(os.totalmem());

// Home directory
console.log(os.homedir());

// Uptime - tells you in seconds how long your system has been up
console.log(os.uptime());