// URL Module

const url = require('url');
const myUrl = new URL('http://mywebsite.com:8000/hello.html?id=100&status=active');

// serialized URL
console.log(myUrl.href);
// this will give you the same result as the line above
console.log(myUrl.toString());

// Host (root domain) - host does incl the port
console.log(myUrl.host);

// Hostname - host name does not include the port
console.log(myUrl.hostname);

// Pathname
console.log(myUrl.pathname);

// Serialized query - gives you everything after the question mark in the URL which is the part that indicates the query
console.log(myUrl.search);

// Params object
console.log(myUrl.searchParams);

// Add param
myUrl.searchParams.append('abc', '123');
console.log(myUrl.searchParams);

// Loop through params
myUrl.searchParams.forEach((value,name) => console.log(`${name}: ${value}`));